  const corsOptions = {
    origin: 'http://localhost:3000', // Replace with the origin of your React application
    credentials: true,
  };
  const corsFn = cors(corsOptions);
  



      const handleclick=async(itemid) =>{
        const response = await fetch('https://4ftu78iqf6.execute-api.us-east-1.amazonaws.com/development/username?userid=malathi', {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
          cache: 'no-cache',
          redirect: 'follow',
          referrerPolicy: 'no-referrer',
        });             
           const jsonData=await response.json()
           console.log(jsonData) 
      }
        return (<>
        
        <div>
          <table className='table table-striped'>
            <thead>
              <tr>
                <th>Username</th>
                <th>Age</th>
                <th>Email</th>
              </tr>
            </thead>
            
            <tbody>
      
            {data && data.map((item) =>{
                return(
                <tr onClick={() =>handleclick(item.username.S)}>
                  <td>{item.username.S}</td>
                  <td>{item.age.N}</td>
                  <td>{item.email.S}</td>
                  <td><Link to="/Edit" className='btn btn-primary fw-bold'>Update</Link></td>
                  <td><button className='btn btn-danger fw-bold'>Delete</button></td>
                 </tr>)
               
              })}
             
            </tbody>
          </table>
        </div>
       </>
    )
}
